sp_widget.client_script:
function($rootScope, $scope, $http, $timeout, $location) {
	/* widget controller */
	var c = this;
	c.scanStarted = false;
	c.scanRequested = c.data.scanRequested;
	var shared = c.widget.options.shared;
	shared.scanner = {};

	var opts = {
		// Whether to scan continuously for QR codes. If false, use scanner.scan() to manually scan.
		// If true, the scanner emits the "scan" event when a QR code is scanned. Default true.
		continuous: true,

		// The HTML element to use for the camera's video preview. Must be a <video> element.
		// When the camera is active, this element will have the "active" CSS class, otherwise,
		// it will have the "inactive" class. By default, an invisible element will be created to
		// host the video.
		video: document.getElementById('preview'),

		// Whether to horizontally mirror the video preview. This is helpful when trying to
		// scan a QR code with a user-facing camera. Default true.
		mirror: false,

		// Whether to include the scanned image data as part of the scan result. See the "scan" event
		// for image format details. Default false.
		captureImage: false,

		// Only applies to continuous mode. Whether to actively scan when the tab is not active.
		// When false, this reduces CPU usage when the tab is not active. Default true.
		backgroundScan: true,

		// Only applies to continuous mode. The period, in milliseconds, before the same QR code
		// will be recognized in succession. Default 5000 (5 seconds).
		refractoryPeriod: 5000,

		// Only applies to continuous mode. The period, in rendered frames, between scans. A lower scan period
		// increases CPU usage but makes scan response faster. Default 1 (i.e. analyze every frame).
		scanPeriod: 10
	};

  c.stopScan = function() {
		c.scanStarted = false;
		c.scanRequested = false;
		shared.scanner.stop();
	};
	c.startScan = function() {
		c.scanRequested = true;
		var timerId = setInterval(function(){
			// wait until the video tag exists
			if(!($scope.videoEl = $("video")[0])) {
				return;
			}
			clearInterval(timerId);

			// instanciate instascan
			var opts = {
				continuous: true,
				video: $scope.videoEl,
				mirror: false,
				captureImage: false,
				backgroundScan: true,
				refractoryPeriod: 5000,
				scanPeriod: 5
			};
			shared.scanner = new Instascan.Scanner(opts);

			shared.scanner.addListener('active', function (content) {
				console.log("**instascan is active");
			});
			shared.scanner.addListener('inactive', function (content) {
				c.scanRequested = false;
				c.scanStarted = false;
			});
			shared.scanner.addListener('scan', function (content) {
				console.log("**instascan found " + content);
				shared.scanner.stop();
				$rootScope.$broadcast('scanner.found', content);
			});
			Instascan.Camera.getCameras().then(function (cameras) {
				if (cameras.length > 0) {
					// grab the environment camera if available; doesnt work on IOS :(
					shared.scanner.start(cameras.length > 1 ? cameras[1] : cameras[0]);
					c.scanStarted = true;
				} else {
					c.scanRequested = false;
					c.scanError = true;
					console.error('**instascan No cameras found.');
				}
			}).catch(function(error) {
					shared.scanner.stop();
					c.scanRequested = false;
					c.scanError = true;
					console.error(error);				
			});	
		}, 1000);	
	};

	$timeout(function() {
		if(c.scanRequested) c.startScan();
	});
}

sp_widget.script:
(function() { 
	data.scanRequested = false;
	if (input !== undefined && input.scanNow == true) {
		data.scanRequested = true;
	}
})();
